import { useState } from "react";
import {v4 as uuidV4} from 'uuid';
import './listcomponent.css'
import Checkbox from '@material-ui/core/Checkbox';
import {connect} from 'react-redux';
import { getItems } from "../../actions/itemAction";
import PropTypes from 'prop-types';

const ListComponent=()=>{

   const handleInput=(e)=>{
       e.preventDefault();
       const input=document.getElementById('add_todo_input');
       setItems([...items,{name:input.value,id:uuidV4()}]);
       input.value="";
       
   }
   const handleDelete=(id)=>{
     setItems(items.filter(item=>item.id!==id));
   }
  
   return (
       <div className="list__components">
           <div className="container">
                 <div className="add_new_todo">
                     <form>
                           <input id="add_todo_input" name="name"/>
                           <button id="add_todo_button"onClick={handleInput}>Add</button>
                    </form>
                </div>
                {items.map(item=>(
                    <div key={item.id} className="todo_item">
                        <Checkbox onClick={()=>handleDelete(item.id)} color="default"/>
                       <p> {item.name}</p> 
                    </div>
                )
                )}
           </div>
         
         
       </div>
   );

 
}
const mapStateToProps=(state)=>({
    item:state.item
})
export default connect(mapStateToProps,{getItems})(ListComponent);
