import {ADD_ITEM,DELETE_ITEM,ITEMS_LOADING} from './types';


export const deleteItems=(id)=>{
    return {
        type:DELETE_ITEM,
        payload:id
    }
}
export const addItem=(name,id)=>{
    return {
        type:ADD_ITEM,
        payload:{
            name,
            id
        }
    }
}
async function setItemsLoading(){
    var data;
    await axios.get('/api.items').then(res=>{
        data=res.data;
    })
    return {
        type:ITEMS_LOADING,
        payload:data
    }
}
export {setItemsLoading};
