import {ADD_ITEM,DELETE_ITEM,ITEMS_LOADING} from './types';


export const deleteItems=(id)=>{
    return {
        type:DELETE_ITEM,
        payload:id
    }
}
export const addItem=(name,id)=>{
    return {
        type:ADD_ITEM,
        payload:{
            name,
            id
        }
    }
}
export const setItemsLoading=()=>{
    return {
        type:ITEMS_LOADING
    }
}
