import { useState } from "react";
import {v4 as uuidV4} from 'uuid';
import './listcomponent.css'
import Checkbox from '@material-ui/core/Checkbox';
const ListComponent=()=>{
   const [items,setItems]=useState([
       {
           name:'do homework',
           id:uuidV4()
        },
        {
            name:'purchase books',
            id:uuidV4()
         },
         {
            name:'do purchase books',
            id:uuidV4()
         },
         {
            name:'do excercise',
            id:uuidV4()
         }
   ]);
   const handleInput=()=>{
       e.preventDefault();
        document.getElementById('add_todo_input').addEventListener('submit',(e)=>{
            console.log(e);
        })
   }
   return (
       <div className="list__components">
           <div className="container">
                 <div className="add_new_todo">
                           <input id="add_todo_input" val="" onChange={(e)=>{console.log(e.target.val)}}/>
                           <button id="add_todo_button" onClick={handleInput}>Add</button>
                </div>
                {items.map(item=>(
                    <div key={item.key} className="todo_item">
                        <Checkbox color="default"/>
                       <p> {item.name}</p> 
                    </div>
                )
                )}
           </div>
         
         
       </div>
   )
}
export default ListComponent;
