import {v4 as uuidV4} from 'uuid';
import {GET_ITEMS,ADD_ITEM,DELETE_ITEM} from '../actions/types';

const initialState ={
    items: [
        {
            name:'do homework',
            id:uuidV4()
         },
         {
             name:'purchase books',
             id:uuidV4()
          },
          {
             name:'do purchase books',
             id:uuidV4()
          },
          {
             name:'do excercise',
             id:uuidV4()
          }
    ]
}

export default function(state=initialState,action){
   switch(action.type){
       case GET_ITEMS:
           return {
               ...state
           }
        case DELETE_ITEM:
            return {   

                items: [
                    {
                        name:'do homework',
                        id:uuidV4()
                     },
                     {
                         name:'purchase books',
                         id:uuidV4()
                      },
                      {
                         name:'do purchase books',
                         id:uuidV4()
                      },
                     
                ]
            
            }
        case ADD_ITEM:
            return {
                ...state
            }
        default:
            return state;
   }
}
