import {v4 as uuidV4} from 'uuid';
import './listcomponent.css'
import Checkbox from '@material-ui/core/Checkbox';
import {useSelector,useDispatch} from 'react-redux';
import { deleteItems ,addItem} from "../../actions/itemAction";

const ListComponent=()=>{

    const items= useSelector(state=>state.item.items);
    const dispatch=useDispatch();

   const handleInput=(e)=>{
       e.preventDefault();
       const value=document.getElementById('add_todo_inout').value;
       dispatch(addItem(value,uuidV4()));
   }
  
  
   return (
       <div className="list__components">
           <div className="container">
                 <div className="add_new_todo">
                     <form>
                           <input id="add_todo_input" name="name"/>
                           <button id="add_todo_button"onClick={handleInput}>Add</button>
                    </form>
                </div>
                {items.map(item=>(
                    <div key={item.id} className="todo_item">
                        <Checkbox onClick={()=>dispatch(deleteItems(item.id))} color="default"/>
                       <p> {item.name}</p> 
                    </div>
                )
                )}
           </div>
         
         
       </div>
   );

 
}

export default ListComponent;
